mapper.select:
select
distinct
i.id, i.site, i.channel, case when i.isquote = 1 then t1.ttitle else i.title end title, i.shortTitle, i.titleColor, i.titleBlod, i.source, i.author, i.description, i.tags, i.img, i.url, i.attchs, case when i.isquote = 1 then t1.taddtime else i.addtime end addtime, i.templet, i.isTop, i.ishot,i.isimgs, i.topEndTime, i.clickNum, i.addUser,i.state,i.issign, i.iscomment,i.video,i.indexnum,i.opentype, i.opentimetype,i.openendtime,i.htmlIndexnum,i.ischeck, case when i.isquote = 1 then t1.teditor else i.editor end editor , i.keywords,i.firstvalue, i.isshowindex,i.isshowlist,i.isrecommend,i.gzlm,i.bjyx,i.nper,i.modifier,i.mdtime,i.isshownewscenter, i.istopnewscenter, i.isrecommendnewscenter,i.isquote,i.quoteinfoid,i.recommendreason,i.suitage,i.depth,c.pagemark channelPagemark,c.name channelname,c.indexnum channelIndexnum from freecms_info i left join freecms_channel c on i.channel=c.id left join (select id as tid, title as ttitle,editor as teditor,addtime as taddtime from freecms_info) t1 on i.quoteinfoid = t1.tid
order by ${orderByClause}
limit ${skip},${pageSize}


select

  distinct

,c.pagemark channelPagemark,c.indexnum channelIndexnum
from freecms_info i left join freecms_channel c on i.channel=c.id

  


  order by ${orderByClause}

limit ${start},${pageSize}

  	SELECT COUNT(id) countnum,DATE_FORMAT(ADDTIME, '%Y') AS datename FROM freecms_info i 
    
      
    
	GROUP BY DATE_FORMAT(ADDTIME, '%Y')
    limit ${skip},${pageSize}

	SELECT COUNT(id) countnum,DATE_FORMAT(ADDTIME, '%Y-%m') AS datename FROM freecms_info i
    
      
    
	GROUP BY DATE_FORMAT(ADDTIME, '%Y-%m')
    limit ${skip},${pageSize}

	SELECT COUNT(id) countnum,DATE_FORMAT(ADDTIME, '%Y-%m-%d') AS datename FROM freecms_info i
    
      
    
	GROUP BY DATE_FORMAT(ADDTIME, '%Y-%m-%d')
    limit ${skip},${pageSize}

mapper.update:

